  -- =============================================
  ON CONFLICT (slug) DO UPDATE SET logo_url = EXCLUDED.logo_url, tagline = EXCLUDED.tagline, description = EXCLUDED.description;

  -- =============================================
  -- PRODUCTIVITY (20 tools)
  -- =============================================
  ON CONFLICT (slug) DO UPDATE SET logo_url = EXCLUDED.logo_url, tagline = EXCLUDED.tagline, description = EXCLUDED.description;

  -- =============================================
  -- DESIGN & CREATIVE (17 tools)
  -- =============================================
  ON CONFLICT (slug) DO UPDATE SET logo_url = EXCLUDED.logo_url, tagline = EXCLUDED.tagline, description = EXCLUDED.description;

